class Solution {
    public int mySqrt(int x) {
        if (x == 0) {
            return 0;
        }
        double x0 = x, C = x;
        while (true) {
            double x1 = (x0 + C / x0) / 2;
            if (Math.abs(x1 - x0) < 1e-7) {
                break;
            }
            x0 = x1;
        }
        return (int)x0;
    }
}
