class MyQueue {
public:
    MyQueue() {

    }

    void push(int x) {
        s1.push(x);
    }

    int pop() {
        if (s2.empty()) {
            while(!s1.empty()){
                s2.push(s1.top());
                s1.pop();
            }
        }
        int ret_val = s2.top();
        s2.pop();
        return ret_val;
    }

    int peek() {
        if (s2.empty()) {
            while(!s1.empty()){
                s2.push(s1.top());
                s1.pop();
            }
        }
        return s2.top();
    }

    bool empty() {
        return s1.empty() && s2.empty();
    }
    stack<int> s1 , s2;
};
