class Solution {
public:
    ListNode* swapPairs(ListNode* head) {
        // 新建一个哨兵节点
        ListNode org_head;
        org_head.next = head;
        ListNode* p_head =  &org_head;
        // 每次遍历当前节点的后两个节点，直到后面只有一个，或者一个都没有
        while(p_head->next != nullptr && p_head->next->next != nullptr) {
            ListNode* node1 = p_head->next , *node2 = p_head->next->next;
            ListNode* tmp = node2->next;
            node2->next = node1;
            node1->next = tmp;
            p_head->next = node2;
            p_head = p_head->next->next;
        }
        return org_head.next;
    }
};
```

