class Solution {
public:
    int sumNumbers(TreeNode* root, int father_num) {
        //结束条件
        if (root == nullptr) {
            return 0;
        }
        // 递归条件
        int sum_num =  father_num*10 + root->val;
        if (root->left == nullptr && root->right == nullptr) {
            return sum_num;
        }
        return sumNumbers(root->left, sum_num) + sumNumbers(root->right, sum_num);

    }

    int sumNumbers(TreeNode* root) {
        return sumNumbers(root, 0);
    }
};
