class Solution {
public:
    int findBottomLeftValue(TreeNode* root) {
        // BFS 一层一层的遍历，最下面一层第一个
        queue<TreeNode*> que;
        que.push(root);
        int first_val = 0;
        while (!que.empty()) {
            // 弹出第一个
            queue<TreeNode*> que_new;
            first_val = que.front()->val;
            while(!que.empty()) {  // 遍历上一层
                TreeNode* node = que.front();
                if (node->left) {
                    que_new.push(node->left);
                } 
                if (node->right) {
                    que_new.push(node->right);
                }
                que.pop();
            }
            que = que_new;  // 将下一层赋值给上一层
        }
        return first_val;
    }
};
```

