/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    // DFS 遍历所有节点，并且把节点坐标放到暂存区中
    void DFS(TreeNode* root, int x, int y) {
        if (root == nullptr) {
            return;
        }
        ValNode val_node{.x = x, .y=y, .val=root->val};
        dp.emplace_back(val_node);
        DFS(root->left, x+1, y-1);
        DFS(root->right, x+1, y+1);
    }
    vector<vector<int>> verticalTraversal(TreeNode* root) {
        DFS(root, 0, 0);
        // 排序dp 按照  y x val 排序
        std::sort(dp.begin(), dp.end(), [](const ValNode& a, const ValNode&b){
            if (a.y < b.y) {
                return true;
            } else if (a.y > b.y) {
                return false;
            } else {
                if (a.x < b.x) {
                    return true;
                } else if (a.x > b.x) {
                    return false;
                } else {
                    return a.val <= b.val;
                }
            }
        });
        // 打印
        vector<vector<int>> ret_vec;
        int last_y =  -10001;
        vector<int> tmp_dp;
        for (const auto& node_val : dp) {
            if (node_val.y != last_y && !tmp_dp.empty()) {
                ret_vec.emplace_back(tmp_dp);
                tmp_dp.clear();
            }
            tmp_dp.emplace_back(node_val.val);
            last_y = node_val.y;
        }
        if (!tmp_dp.empty()) {
            ret_vec.emplace_back(tmp_dp);
        }
        return ret_vec;
    }
private:
    struct ValNode {
        int x;
        int y;
        int val;
    };
    vector<ValNode> dp;
};
```

