class Solution {
public:
// hash + 堆排序
    vector<int> topKFrequent(vector<int>& nums, int k) {
        map<int, int> hash;
        // hash 表
        for (const int& num: nums) {
            hash[num]++;
        }
        // 小顶堆
        priority_queue<pair<int,int>, vector<pair<int,int>>, greater<pair<int,int>>> small_queue;
        for ( auto& [num, size] : hash) {
            if (small_queue.size() < k) {
                small_queue.push(make_pair(size, num));
            } else if (size > small_queue.top().first) {
                small_queue.pop();
                small_queue.push(make_pair(size, num));
            }
        }
        // 输出
        vector<int> ret_vec;
        while(small_queue.size()) {
            ret_vec.emplace_back(small_queue.top().second);
            small_queue.pop();
        }
        return ret_vec;
    }
};
