class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        int[] num2 = convertNumToArray(k);
        List<Integer> result = new ArrayList<>();
        int i = num.length - 1;
        int j = num2.length - 1;
        int carry = 0;
        while (i >= 0 || j >= 0) {
            int sum = carry;
            if (i >= 0) {
                sum += num[i];
            }
            if (j >= 0) {
                sum += num2[j];
            }
            result.add(sum % 10);
            carry = sum / 10;
            i--;
            j--;
        }
        if (carry > 0) {
            result.add(carry);
        }
        Collections.reverse(result);
        return result;
    }


    private int[] convertNumToArray(int num) {
        List<Integer> result = new LinkedList<>();
        while (num > 0) {
            result.add(num % 10);
            num /= 10;
        }
        Collections.reverse(result);
        return result.stream().mapToInt(Integer::intValue).toArray();
