class Solution {
    public int searchInsert(int[] nums, int target) {
        int len = nums.length;
        if(len == 0){
            return -1;
        }
        if(nums[len - 1] < target){
            return len;
        }
        int left, right, mid;
        left = 0;
        right = len - 1;
        while(left < right){
            mid = (left + right) >>> 1;
            if(nums[mid] == target){
                return mid;
            }else if(nums[mid] < target){
                left = mid + 1;
            }else{
                right = mid;
            }
        }
        return left;
    }
}
