		curB = curB.next;
		lenB++;
	}
	curA = headA;
	curB = headB;
	if (lenA < lenB) {
		[curA, curB] = [curB, curA];
		[lenA, lenB] = [lenB, lenA];
	}
	diff = lenA - lenB;
	while (diff-- > 0) {
		curA = curA.next;
	}
	while (curB) {
