/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */

/**
 * Encodes a tree to a single string.
 *
 * @param {TreeNode} root
 * @return {string}
 */
var serialize = function(root) {
  let sb = [];
  const dfs= (node) => {
    if (node === null) {
      sb.push("null");
      return;
    }
    sb.push(node.val);
    dfs(node.left);
    dfs(node.right);
  };
  dfs(root);
  return sb.join(',');
};

/**
 * Decodes your encoded data to tree.
 *
 * @param {string} data
 * @return {TreeNode}
 */
var deserialize = function(data) {
  let nodes = data.split(',');
  let index = 0
  const dfsBuild = () => {
    if (nodes[index] === "null") {
      index += 1
      return null;
    }
    
    let currentNode = new TreeNode(Number(nodes[index++]));
    currentNode.left = dfsBuild();
    currentNode.right = dfsBuild();
    return currentNode;
  };
  return dfsBuild();
};

/**
 * Your functions will be called as such:
 * deserialize(serialize(root));
 */
Time: O(n)
Space: O(n)
