function lengthOfLongestSubstring() {
  if (s.length === 0) return 0;
  const map = new Map();
  let i = 0;
  let j = 0;
  let totalLen = 0;
  
  while (j < s.length) {
    const curr = map.get(s[j]);
    if (curr !== undefined && curr >= i) { 
      i = curr + 1;
    }
    
    totalLen = Math.max(totalLen, j - i);
    
    map.set(s[j], j);
    j++;
  }
  
  return totalLen + 1;
};
