var maxSlidingWindow = function(nums, k) {
    const q = [];
    const res = [];
    
    let l = 0;
    for (let i = 0; i < nums.length; i++) {
        while (q && nums[q[q.length - 1]] <= nums[i]) {
            q.pop();
        }
        q.push(i); // we will push indices which will make it easier to compare q values with window pointers

        // if first element out of bounce
        if (l > q[0]) {
            q.shift();
        }
        // will get the window size
        if (l === i - k + 1) {
            // push first element from our q
            res.push(nums[q[0]]);
            // shrink window
            l++;
        }
    }
    return res;
};
