var mySqrt = function(x) {
  let left=1,right=x
  while(left<=right){
    let mid=left+((right-left)>>1)
    //防止溢出的中间值求法
    if(mid*mid<=x){
      if((mid+1)*(mid+1)>x){
        return mid
        //当前数大于，下个数小于，则为target
      }
      left=mid+1
      //小于就向右找
    }
    else{
      right=mid-1
      //大于就向左找
    }
