            return 0
        level = 1
        ans = 1
        stack = [(root,level)]
        while stack:
            node,level = stack.pop()
            ans = max(level,ans)
            if node.left:
                stack.append((node.left,level+1))
            if node.right:
                stack.append((node.right,level+1))
