public:
    int maxChunksToSorted(vector<int>& arr) {
        stack<int> st;
        st.push(arr[0]);
        for(int i = 1; i < arr.size(); i++) {
            if(arr[i] < st.top()) {
                int maxNum = st.top();
                while(!st.empty() && st.top() > arr[i])  st.pop();
                st.push(maxNum);
            }
            else st.push(arr[i]);    
        }
