```cpp
class CustomStack {
public:
    CustomStack(int maxSize) {
        element = new int[maxSize];
        top_stack = -1;
        maxsize = maxSize;
    }

    void push(int x) {
        if(top_stack == maxsize - 1)
        {
            return;//超过容量，不进行操作
        }
        element[++top_stack] = x;
    }

    int pop() {
        if(top_stack < 0)
        {
            return -1;
        }
        return element[top_stack--];
    }

    void increment(int k, int val) {
        if(top_stack < k - 1) //按照最短长度进行累加
        {
            for(int i = 0; i <= top_stack; i++)
            {
                element[i] += val;
            }
        }
        else
        {
            for(int i = 0; i < k; i++)
            {
                element[i] +=val;
            }
        }
    }
private:
    int* element;//数组
    int top_stack;//栈顶
    int maxsize;//最大容量
};
