class MyQueue {
public:
    MyQueue() {

    }

    void push(int x) {
        s1_input.push(x);
    }

    int pop() {
        if(s1_input.size() == 0 &&s2_output.size() == 0)
        {//判断队列中是否有元素
            exit(0);
        }
        if(!s2_output.empty())//输出栈有元素就直接从输出栈输出
        {
            int i = s2_output.top();
            s2_output.pop();
            return i;
