            while(!s1_input.empty())
            {
                s2_output.push(s1_input.top());
                s1_input.pop();
            }
            int i = s2_output.top();
            s2_output.pop();
            return i;
        }
    }

    int peek() {//和pop函数一样，少了删除
        if(s1_input.size() == 0 &&s2_output.size() == 0)
        {
            exit(0);
        }
        if(!s2_output.empty())
        {
            int i = s2_output.top();
            return i;
        }
        else
        {
            while(!s1_input.empty())
            {
                s2_output.push(s1_input.top());
                s1_input.pop();
            }
            int i = s2_output.top();
            return i;
        }
    }

    bool empty() {
        return (s1_input.empty() && s2_output.empty());
    }
private:
    stack<int> s1_input;
    stack<int> s2_output;
};
```
