class Solution {
public:
    ListNode* swapPairs(ListNode* head) {
        if(head == nullptr||head->next == nullptr )
        {
            return head;
        }
        ListNode* pre_head = new ListNode;//创建空节点使头节点的操作和另外节点的操作统一
        pre_head->next = head;
        ListNode* tem = pre_head;
        while(tem->next !=nullptr&&tem->next->next !=nullptr)
        {
            ListNode* first = tem->next;
            ListNode* last = tem->next->next;

            first->next = last->next;
            last->next = first;
            tem->next = last;
            tem = first;
        }
        return pre_head->next;
    }
};
