class Solution {
public:
    int findBottomLeftValue(TreeNode* root) {
        int left;
        queue<TreeNode*> q;
        q.push(root);
        while(!q.empty())
        {
            int size = q.size();
            for(int i = 0; i<size; i++)//每一层的进行循环
            {
                TreeNode* cur = q.front();
                q.pop();
                if(cur->left != nullptr)
                {
                    q.push(cur->left);
                }
                if(cur->right != nullptr)
                {
                    q.push(cur->right);
                }
                if(i == 0)//最左边的值
                {
                    left = cur->val;
                }
            }
        }
        return left;
    }
};
