class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        unordered_map<char,int> window;
        int left = 0, right = 0;
        int len = 0;
        while(right < s.length())
        {
            char c = s[right];
            window[c]++;
            right++;
            //收缩左侧
            while(window[c] > 1)
            {
                char d = s[left];
                left++;
                window[d]--;
            }
            len = max(len,right - left);
        }
        return len;
    }
};
