# 思路
组间拓扑排序，组内拓扑排序
```cpp
class Solution {
public:
    vector<int> topsort(vector<int>& degree, vector<vector<int>>& graph, vector<int>& items)
    {
        queue<int> Q;
        for(auto& a:items)
        {
            if(degree[a] == 0)
            {
                Q.push(a);
            }
        }
        vector<int> res;
        while(!Q.empty())
        {
            int cur_id = Q.front();
            Q.pop();
            res.emplace_back(cur_id);
            for(auto& u : graph[cur_id])
            {
                if(--degree[u] == 0)
                {
                    Q.push(u);
                }
            }
        }

        return res.size() == items.size()? res: vector<int>{};
    } 

    vector<int> sortItems(int n, int m, vector<int>& group, vector<vector<int>>& beforeItems) {
        vector<vector<int>> groupitem(n+m);
        //组间和组内的图
        vector<vector<int>> groupgraph(n+m);
        vector<vector<int>> itemsgraph(n);
        //组间和组内的度
        vector<int> groupdegree(n+m, 0);
        vector<int> itemsdegree(n, 0);

        vector<int> id;
        for (int i = 0; i < n + m; ++i) {
            id.emplace_back(i);
        }

        //对无分组的项目进行编号；
        int left = m;
        for(int i = 0; i<n;i++)
        {
            if(group[i] == -1)
            {
                group[i] = left++;
            }
            groupitem[group[i]].emplace_back(i);
        }
        //建立图
        for(int i = 0; i< n;i++)
        {
            int cur_id = group[i];
            for(auto& item : beforeItems[i])
            {
                int pre = group[item];
                if(pre == cur_id)//同组
                {
                    itemsdegree[i] += 1;
                    itemsgraph[item].emplace_back(i);
                }
                else
                {
                    groupdegree[cur_id] +=1;
                    groupgraph[pre].emplace_back(cur_id);//组间关系
                }
            }
        }
        //首先需要对组间进行排序
        vector<int> grouptop = topsort(groupdegree, groupgraph, id);
        if(grouptop.size() == 0)
        {
            return vector<int>{};
        }
        //对组内进行排序
        vector<int> ans;
        for(auto& v : grouptop)
        {
            int size = groupitem[v].size();
            if(size == 0)
            {
                continue;
            }
            vector<int> itemtop = topsort(itemsdegree, itemsgraph, groupitem[v]);
            if(itemtop.size() == 0)
            {
                return vector<int>{};
            }
            for(auto & w : itemtop)
            {
                ans.emplace_back(w);
            }
        }   
        return ans;
    }
};
```
