class Solution {
public:
    int mySqrt(int x) {
        if(x == 0 || x==1)
        {
            return x;
        }
        int left = 0, right = x;
        while(left <= right)
        {
            long long mid = left + (right - left)/ 2;
            long long multi = mid * mid;
            if(multi > x)
            {
                right = mid - 1;
            }
            else if(multi < x)
            {
                if((mid + 1) * (mid+1) > x)
                {
                    return mid;
                }
                left = mid + 1;
            }
            else
            {
                return mid;
            }
        }
        return left;
    }
};
