### 代码



```cpp

class MyQueue {
public:
    MyQueue() {

    }

    void push(int x) {
        m_pushStack.push(x);
    }

    int pop() {
        if (m_popStack.empty()) {
            while (!m_pushStack.empty()) {
           m_popStack.push(m_pushStack.top());
           m_pushStack.pop();
        }
        }

        int temp = m_popStack.top();
        m_popStack.pop();
        return temp;

    }

    int peek() {
        if (!m_popStack.empty()) {
            return m_popStack.top();
        }

        while (!m_pushStack.empty()) {
           m_popStack.push(m_pushStack.top());
           m_pushStack.pop();
        }

        return m_popStack.top();
    }

    bool empty() {
        return m_pushStack.empty() && m_popStack.empty();
    }

private:
    stack<int> m_pushStack;
    stack<int> m_popStack;
};

