/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {
        if (headA == nullptr || headB == nullptr) {
            return nullptr;
        }

        if (headA == headB) {
            return headA;
        }

        ListNode* tmpA = headA;
        ListNode* tmpB = headB;

        while (tmpA != tmpB) {
            if (tmpA == nullptr) {
                tmpA = headB;
            } else {
                tmpA = tmpA->next;
            }

             if (tmpB == nullptr) {
                tmpB = headA;
            } else {
                tmpB = tmpB->next;
            }
        }

        return tmpA;
    }
};

```

