### 代码


```cpp

class LRUCache {
public:
    LRUCache(int capacity) {
        m_capacity = capacity;       
    }

    int get(int key) {
        // 不存在返回-1
        if (m_map.count(key) == 0) {
            return -1;
        }

        m_list.splice(m_list.begin(), m_list, m_map[key]);
        return m_map[key]->second;
    }

    void put(int key, int value) {
        // 先判断有就返回, 没有就需要先判断是否需要逐出，需要就先逐出再插入，逐出链表的末尾元素，再插入
        // 不需要就直接插入,再将key更新到链表的头
        if (get(key) != -1) {  //key存在,调用get时已将缓存移到头部，再更新map
            m_map[key]->second = value;
        } else {   //key不存在
            if(m_list.size() == m_capacity) {
                int delKey = m_list.back().first;
                m_list.pop_back();
                m_map.erase(delKey);
            }

            m_list.emplace_front(key, value); //如果key不存在，在头部加入新的缓存   
            m_map[key] = m_list.begin();
        }
    }

    private:
        int m_capacity { 0 };
        std::list<pair<int, int>> m_list;  //数据用双向链接存储
        std::unordered_map<int, list<pair<int, int>>::iterator> m_map;  //value是list的迭代器
};

/**
