/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Codec {
public:

    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        string ret;
        dfs(root, ret);

        return ret;
    }

    void dfs(TreeNode* root, string &str) {
        if (root == nullptr) {
            str.append("NULL,");
        } else {
            str.append(to_string(root->val));
            str.append(",");
            dfs(root->left, str);
            dfs(root->right, str);
        }
    }


    TreeNode* decode(list<string> &nodeList) {
        if (nodeList.front() == "NULL") {
            nodeList.erase(nodeList.begin());
            return nullptr;
        }

        TreeNode *node = new TreeNode(stoi(nodeList.front()));
        nodeList.erase(nodeList.begin());
        node->left  = decode(nodeList);
        node->right = decode(nodeList);

        return node;  
    }


    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {
        list<string> dataArray;
        string str;
        for (auto& ch : data) {
            if (ch == ',') {
                dataArray.push_back(str);
                str.clear();
            } else {
                str.push_back(ch);
            }
        }
        if (!str.empty()) {
            dataArray.push_back(str);
            str.clear();
        }

        return decode(dataArray);
    }
};

