/**
 * Definition for singly-linked list.
 * class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) {
 *         val = x;
 *         next = null;
 *     }
 * }
 */
public class Solution {
    public static ListNode inCycleNode(ListNode head) {
        ListNode fast = head.next;
        ListNode slow = head;
        while(slow!=null && fast!=null) {
            slow = slow.next;
            fast = fast.next;
            if(slow == fast) return slow;
            if(fast!=null) fast = fast.next;
        }
        return null;
    }

    public static ListNode detectCycle(ListNode head) {
        if(head == null || head.next == null) return null;
        if(head.next == head) return head;
        ListNode inLoopNode = inCycleNode(head);
        if(inLoopNode == null) return null;
        ListNode slow = head;
        ListNode fast = head;
        int count = 1;
        for(ListNode point=inLoopNode; point.next!= inLoopNode; point = point.next) {
            count++;
        }
        for(int i = 0; i < count;i++) {
            fast = fast.next;
        }
        while(slow!=fast) {
            slow = slow.next;
            fast = fast.next;
        }
        return slow;
    }
}
