class CustomStack {
private:
    int size;
    vector<int> stack;
    vector<int> incr;

    // 思路二 另一种写法：incr加一个dummy位，解决溢出
public:
    CustomStack(int maxSize) {
        this->size = 0;
        this->stack.resize(maxSize);
        this->incr.resize(maxSize + 1);
    }

    void push(int x) {
        if (size == stack.size()) return;
        stack[size++] = x;
    }

    int pop() {
        if (size == 0) return -1;
        int ret = stack[size - 1];
        if (incr[size] != 0) {
            ret += incr[size];
            // 因为size == 0 开始handle
            // size == 1, idx = 0, preidx=-1 溢出被dummy(第0位)handle了
            incr[size - 1] += incr[size]; 
            incr[size] = 0;
        }
        size--;
        return ret;
    }

    void increment(int k, int val) {
        incr[min(k, size)] += val;
    }
};

Time: O(1) for all operations
