var detectCycle = function(head) {
    if(head === null) return null;
    let fast = head;
    let slow = head;
    while(fast !== null && fast.next !== null){
        fast = fast.next.next;
        slow = slow.next;
        if(fast === slow){
            //进入这个循环，说明有环
            let p = head;
            while(p != slow){
                p = p.next;
                slow = slow.next;
            }
            return p;
        }
    }
    return null;
};
