var serialize = function(root) {
    let queue = [];
    let res = [];
    queue.push(root);
    while(queue.length > 0){
        let cur = queue.shift();
        if(cur === null){
            res.push("null");
        }
        else{
            res.push(cur.val);
            queue.push(cur.left);
            queue.push(cur.right);
        }
    }
    return res.join(',');
};

var deserialize = function(data) {
    let values = data.split(",");
    if(values[0] === "null") {
        return null;
    }
    let root = new TreeNode(values[0]);
    //保存每一层非空父节点
    let parentsQueque = [root];

    for(let i = 1; i < values.length; i+=2){
        let parent = parentsQueque.shift();
        if(parent != null){
            let leftNode = values[i] === "null" ? null : new TreeNode(values[i]);
            let rightNode = values[i + 1] === "null" ? null : new TreeNode(values[i + 1]);
            parent.left = leftNode;
            parent.right = rightNode;
            if(leftNode !== null) parentsQueque.push(leftNode);
            if(rightNode !== null) parentsQueque.push(rightNode);
        }
        else{
            continue;
        }
    }
    return root;
};
```
