    if(nums.length === 0 || nums.length === 1) return nums.length;
    let fast = 0;
    let slow = 0;
    while(fast < nums.length - 1){
        if(nums[fast] === nums[fast + 1]){
            fast++;
        }
        else{
            slow++;
            fast++;
            nums[slow] = nums[fast];
