var maxSlidingWindow = function(nums, k) {
    let res = [];
    let queue = [];
    //找到第一个滑动窗口的最大值，当nums.length < i < k时，nums[i] = undefined, undefined使用<、>和任何值比较大小均为false
    for(let i = 0; i < k; i++){
        while(queue.length > 0 && nums[queue[queue.length - 1]] < nums[i]){
            queue.pop();
        }
        queue.push(i);
    }

    res.push(nums[queue[0]]);
    for(let right = k; right < nums.length; right++){
        let left = right - k + 1;
        while(queue[0] < left){
            queue.shift();
        }
        while(queue.length > 0 && nums[queue[queue.length - 1]] < nums[right]){
            queue.pop();
        }
        queue.push(right);
        res.push(nums[queue[0]]);
    }
    return res;

};

```
