class Solution:
    def shortestToChar(self, s: str, c: str) -> List[int]:
        P = []
        for i in range(len(s)):
            if s[i] == c:
              P.append(i)
        r = []
        left = 0
        for i in range(len(P)):
            if i==len(P)-1:
                right = len(s)-1
            else:
                right = (P[i]+P[i+1]-1)//2
            j = P[i] - left 
            while j>0:
                r.append(j)
                j-=1
            while j<=right-P[i]:
                r.append(j)
                j+=1
        
            left = right+1
        
        return r
```
