class Solution:
    def sumNumbers(self, root: TreeNode) -> int:
        def dfs(root, cur):
            if not root:                          
            #空节点返回 0 
                return 0
            if not root.left and not root.right: 
            # 找到叶子节点，返回值 
                return cur*10 + root.val
            else:                              
            # 如果非空节点，继续找叶子节点，更新当前父节点的cur值
                return dfs(root.left, cur*10 + root.val) + dfs(root.right, cur*10 + root.val)
    
        return dfs(root, 0)
