class Solution:
    def verticalTraversal(self, root: TreeNode) -> List[List[int]]:
        re = {}
        node_queue = [root]
        col_queue = [0]
        while node_queue:
            length = len(node_queue)
            layer_re = {}
            for _ in range(length):
                
                node = node_queue.pop(0)
                col = col_queue.pop(0)
                if col not in layer_re.keys():
                    layer_re[col] = [node.val]
                else:
                    layer_re[col].append(node.val)
            
                if node.left:
                    node_queue.append(node.left)
                    col_queue.append(col-1)
                if node.right:
                    node_queue.append(node.right)
                    col_queue.append(col+1)
                
            for i in layer_re.keys():
                layer_re[i].sort()
                if i not in re.keys():
                    re[i] = layer_re[i]
                else:
                    re[i].extend(layer_re[i])
            
        Col = list(re.keys())
        Col.sort()
        result = []
        for col in Col:
            result.append(re[col])
        return result 
```

class Solution:
    def topKFrequent(self, nums: List[int], k: int) -> List[int]:
        count = {}
        for num in nums:
            if num in count:
                count[num] += 1
            else:
                count[num] = 1

        Bucket = [[] for _ in range(len(nums))]

        for i in count.keys():
            Bucket[count[i]-1].append(i)
        Result = []
        i = len(nums)-1
        while len(Result)<k:
            Result.extend(Bucket[i])
            i -= 1
        return Result

