public class Solution {
   public int[] shortestToChar_1(String s, char c) {
        int[] ans = new int[s.length()];
        int fir = -1, sec = Integer.MAX_VALUE / 2, cur = 0;
        fir = s.indexOf(c);
        for (int i = 0; i < ans.length; i++) {
            if (fir > i) {
                ans[i] = fir - i;
            } else if (fir == i) {
                ans[i] = 0;
                sec = s.indexOf(c, fir + 1) == -1 ? Integer.MAX_VALUE / 2 : s.indexOf(c, fir + 1);
            } else if (i < sec) {
                ans[i] = Math.min(Math.abs(fir - i), Math.abs(sec - i));
            } else {
                ans[i] = 0;
                fir = sec;
                sec = s.indexOf(c, fir + 1) == -1 ? Integer.MAX_VALUE / 2 : s.indexOf(c, fir + 1);
            }
        }
        return ans;
    }
}
