class CustomStack_PrefixSum {
    private int[] arr;
    private int[] add;
    private int top;

    public CustomStack_PrefixSum(int maxSize) {
        arr=new int[maxSize];
        add=new int[maxSize];
        top=-1;
    }

    public void push(int x) {
        if (top < arr.length-1 ) {
            arr[++top]=x;
        }
    }

    public int pop() {
        if (top >= 0) {
            int res=arr[top]+add[top];
            if (top>=1){
                add[top-1]+=add[top];
            }
            add[top--]=0;
            return res;
        }return -1;
    }

    public void increment(int k, int val) {
        int end= Math.min(k-1, top);
        if (end>=0){
            add[end]+=val;
        }
    }
}
```

