public String decodeString(String s) {
        Stack<Integer> add=new Stack<>();
        Stack<String> str=new Stack<>();
        StringBuilder builder = new StringBuilder();
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i)>=48&&s.charAt(i)<=57){
                int j=i;
                while(s.charAt(j+1)>=48&&s.charAt(j+1)<=57){
                    j++;
                }
                int num=Integer.parseInt(s.substring(i,j+1));
                add.push(num);
                i=j;
            }else if(s.charAt(i)==91){
                int j=i+1;
                String temp = new String("");
                while (s.charAt(j)>=97&&s.charAt(j)<=122){
                    temp+=(s.charAt(j));
                    j++;
                }
                i=j-1;
                str.push(temp);
            }else if(s.charAt(i)==93){
                int time=add.pop();
                String base=str.pop();
                StringBuilder builder1 = new StringBuilder();
                while (time>0){
                    builder1.append(base);
                    time--;
                }
                if (!str.isEmpty()){
                    base=str.pop();
                }
                else base="";
                base+=builder1.toString();
                str.push(base);
            }else {
                String t=new String("");
                if (!str.isEmpty()){
                    t = str.pop();
                }
                t+=s.charAt(i);
                str.push(t);
            }
        }
        return str.peek();
    }
