public int maxChunksToSorted(int[] arr) {
        Stack<Pair<Integer, Integer>> stk = new Stack<>();
        int[] sorted ;
       sorted =arr.clone();
       BoboNote.DataStructure.Arrays.sort(sorted);
        int[] indexs = new int[arr.length];
        for (int i = 0; i < arr.length; i++) {
            int index =Search(sorted, arr[i]);
            indexs[i]=index;
        }
        for (int i = 0; i < arr.length; i++) {
            int left=Math.min(i,indexs[i]),right=Math.max(i,indexs[i]);
            if (stk.isEmpty()){
                stk.push(new Pair<>(left,right));
            }else{
                Pair<Integer, Integer> idx = stk.pop();
                int preL=idx.getKey();
                int preR=idx.getValue();
                if (preL <= left && preR >= left && right > preR) {
                    stk.push(new Pair<>(preL,right));
                }
                else if (left>preR){
                    stk.push(new Pair<>(preL,preR));
                    stk.push(new Pair<>(left,right));
                }else {
                    stk.push(new Pair<>(preL,preR));
                }
            }
        }

        return stk.size();
    }
     private int Search(int[] sorted, int a) {
        int i=0;
        while (sorted[i++]!=a);
        sorted[--i]=-1;
        return i;
    }
