```java
public class LRUCache_custom {

    class DoubleLinkedNode{
        int val;
        int key;
        DoubleLinkedNode prev;
        DoubleLinkedNode next;

        public DoubleLinkedNode() {
        }

        public DoubleLinkedNode(int key,int val) {
            this.key = key;
            this.val = val;

        }
    }

    private int capacity;
    private int size;
    private DoubleLinkedNode dummyHead,dummyTail;
    private HashMap<Integer,DoubleLinkedNode> map;


    public LRUCache_custom(int capacity) {
        this.capacity=capacity;
        size=1;
        map=new HashMap<>();
        dummyHead=new DoubleLinkedNode();
        dummyTail=new DoubleLinkedNode();
        dummyHead.next=dummyTail;
        dummyTail.prev=dummyHead;
    }

    public int get(int key) {
        DoubleLinkedNode node = map.get(key);
        if (node ==null)return -1;
        moveToHead(node);
        return node.val;
    }

    public void put(int key, int value) {
        DoubleLinkedNode node = map.get(key);
        if (node!=null){
            node.val=value;
            moveToHead(node);
        }
        else {
            if (size<capacity){
                size++;
            }else {
               DoubleLinkedNode tail=removeTail();
               map.remove(tail.key);

            }
        }
        DoubleLinkedNode node1 = new DoubleLinkedNode(key, value);
        map.put(key,node1);
        addToHead(node1);
    }

    private DoubleLinkedNode removeTail() {
        DoubleLinkedNode res = dummyTail.prev;
        removeNode(res);
        return res;
    }

    private void moveToHead(DoubleLinkedNode node) {
        removeNode(node);
        addToHead(node);
    }
    private void removeNode(DoubleLinkedNode node) {
        node.prev.next=node.next;
        node.next.prev=node.prev;
    }
    private void addToHead(DoubleLinkedNode node) {
        node.prev=dummyHead;
        node.next=dummyHead.next;
        dummyHead.next.prev=node;
        dummyHead.next=node;
    }
}

```

