public int sumNumbers(TreeNode root) {
        if (root==null)return 0;
        return sumNumbers(root,0);
    }
    public int sumNumbers(TreeNode root,int base) {
        if (root.left==null&&root.right==null)return base*10+root.val;
        int sum=0;
        if (root.left!=null){
            sum+=sumNumbers(root.left,base*10+root.val);
        }
        if (root.right!=null){
            sum+=sumNumbers(root.right,base*10+root.val);
        }
        return sum;
    }
```

