        int m = b.size();
        int l[26] = {0};
        int r[26] = {0};
        for (auto &x : a) {
            l[x - 'a']++;
        }
        for (auto &x : b) {
            r[x - 'a']++;
        }
        int lSum = 0;
        int rSum = 0;
        int ans = n + m;
        for (int i = 0; i < 25; ++i) {
            lSum += l[i];
            rSum += r[i];
            ans = min(ans, min(min(n + m - l[i] - r[i], n - lSum + rSum), m - rSum + lSum));
        }
        ans = min(ans, n + m - l[25] - r[25]);
        return ans;
    }
