class Solution {
    public int findBottomLeftValue(TreeNode root) {
        if (root == null) {
            return -1;
        }

        Queue<TreeNode> q = new ArrayDeque<>();
        q.offer(root);
        int left = 0;
        while(!q.isEmpty()) {
            int levelSize = q.size();
            left = q.peek().val;
            for(int i = 0; i < levelSize; i++) {
                TreeNode curr = q.poll();
                if (curr.left != null) {
                    q.offer(curr.left);
                }
                if (curr.right != null) {
                    q.offer(curr.right);
                }
            }
        }
        return left;
    }
}
