class Solution {
    public int mySqrt(int x) {
        int left = 0;
        int right = x;

        int res = -1;
        while(left <= right) {
            int mid = left + (right - left) / 2;
            if ((long) mid * mid <= x) {
                res = mid; // keep the integer part
                left = mid + 1;
            } 
            else {
                right = mid - 1;
            }
        }
        return res;
    }
}
