class Solution {
    public int findJudge(int n, int[][] trust) {
        int[] inDegress = new int[n + 1];
        int[] outDegress = new int[n + 1];
        for(int[] edge : trust){
            int x = edge[0], y = edge[1];
            inDegress[y]++;
            outDegress[x]++;
        }
        for(int i = 1; i <= n; i++){
            if(inDegress[i] == n - 1 && outDegress[i] == 0){
                return i;
            } 
        }
        return -1;
    }
}
