            if (s.charAt(i) == c) {
                pre = i;
            }
            res[i] = i - pre;
        }
        pre = Integer.MAX_VALUE / 2;
        for (int i = len - 1; i >= 0; i--) {
            if (s.charAt(i) == c) {
                pre = i;
            }
            res[i] = Math.min(res[i], pre - i);
        }
        return res;
    }
}

class Solution {
    public ListNode swapPairs(ListNode head) {
      ListNode dummpyHead=new ListNode(0);
      dummpyHead.next=head;
      ListNode temp=dummpyHead;
      while(temp.next!=null&&temp.next.next!=null){
        ListNode node1=temp.next;
        ListNode node2=temp.next.next;
          node1.next=node2.next;
          node2.next=node1;
          temp.next=node2;
          temp=node1;
      }
      return dummpyHead.next;

    }
}

class Solution {
    public List<Integer> findSubstring(String s, String[] words) {
        HashMap<String,Integer> map = new HashMap<>();
        int n = s.length();
        int sl = words[0].length();
        List<Integer> res = new ArrayList<>();

        if (n < sl){
            return new ArrayList<>();
        }

        for (String str : words) {
            map.put(str,map.getOrDefault(str,0)+1);
        }

        for (int i = 0; i < n; i++) {
            int x = i;
            if (i + sl * words.length > n)
                break;
            String str = s.substring(x,x+sl);
            HashMap<String,Integer> tmp = new HashMap<>(map);

            while (!tmp.isEmpty() && tmp.containsKey(str)) {
                x = x + sl;
                tmp.put(str,tmp.get(str) - 1);
                if (tmp.get(str) == 0){
                    tmp.remove(str);
                }
                if (x + sl > n) {
                    break;
                }
                str = s.substring(x,x+sl);
            }

            if (tmp.isEmpty()){
                res.add(i);
            }
        }
        return res;
    }
}

