/**
 * @param {string} s
 * @param {character} c
 * @return {number[]}
 */
var shortestToChar = function(s, c) {
    // 转换为数组
    // 遍历找出所有c的索引
    // 遍历数组，比较距离，取最小值
    const strArray = s.split('');
    let cIndexArray = [];

    strArray.forEach((s, i) => {
        if (s === c) {
            cIndexArray.push(i);
        }
    })
    let result = [];
    strArray.forEach((s, i) => {
        let diff = Infinity;
        cIndexArray.forEach((cIndex) => {
            diff = Math.min(diff, Math.abs(cIndex - i));
        })

        result.push(diff);
    })

    return result;
};

