```javascript
function rotateRight(head: ListNode | null, k: number): ListNode | null {

    let len: number = 0;
    let point: ListNode = head;
    let newHead: ListNode = head;
    let newTail: ListNode;
    let tail: ListNode;
    const realK = k % len;

    if (head === null || realK === 0) {
        return head;
    }

    while (point) {
        len++;
        point = point.next;
    }

    for (let index = 0; index < len - realK; index++) {

        newTail = newHead;
        newHead = newHead.next;

    }

    tail = newHead;
    while (tail.next) {
        tail = tail.next;
    }

    newTail.next = null;
    tail.next = head;

    return newHead;
};
```

```typescript
function swapPairs(head: ListNode | null): ListNode | null {

    if (head === null || head.next === null) {
        return head;
    }

    let point: ListNode = head;
    let newHead: ListNode = point.next;
    point.next = newHead.next;
    newHead.next = point;

    let pre: ListNode = point;
    point = pre.next;

    let len: number = 1;
    head = newHead;
    while (head.next) {
        head = head.next;
        len++;
    }

    let odd: boolean = len % 2 === 0 ? false : true;
    let loopCount: number = odd ? Math.floor(len / 2) - 1 : Math.floor(len / 2) - 2;

    while (loopCount > 0) {
        pre.next = point.next;
        point.next = point.next.next;
        pre.next.next = point;

        pre = point;
        point = pre.next;
        loopCount--;
    }

    if (!odd && point !== null) {
        pre.next = point.next;
        pre.next.next = point;
        point.next = null;
    }

    return newHead;
};
```

```javascript
function sortedListToBST(head: ListNode | null): TreeNode | null {
    let tree_root: TreeNode = new TreeNode();
    let data_arr = [];

    if (head === null) {
        return null;
    }

    while (head !== null) {
        data_arr.push(head.val);
        head = head.next;
    }

    convertBST(data_arr, tree_root, 0, data_arr.length);
    return tree_root;

};

function convertBST(arr_tree: number[], root: TreeNode, start_position: number, convert_length: number) {
    let left_length: number;
    let right_length: number;
    let tree_left: TreeNode = new TreeNode();
    let tree_right: TreeNode = new TreeNode();
    root.left = null;
    root.right = null;

    root.val = arr_tree[start_position + Math.floor(convert_length / 2)];
    left_length = Math.floor(convert_length / 2);
    right_length = convert_length - left_length - 1;
    if (left_length > 0) {
        root.left = tree_left;
        convertBST(arr_tree, root.left, start_position, left_length);
    }
    if (right_length > 0) {
        root.right = tree_right;
        convertBST(arr_tree, root.right, start_position + left_length + 1, right_length);
    }
}
